mapper.select:
		select
	        id,
	        exam_courseid,
	        speciality_recordid,
	        operator,
	        update_date
		 from exa_exam_course_speciality

         and enabled_flag=1
		   and id = #{id} 
		   and exam_courseid = #{examCourseid} 
		   and speciality_recordid = #{specialityRecordid} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select count(*) from exa_exam_course_speciality

             and enabled_flag=1
		   and id = #{id} 
		   and exam_courseid = #{examCourseid} 
		   and speciality_recordid = #{specialityRecordid}

mapper.update:
update exa_exam_course_speciality 

	exam_courseid = #{examCourseid}, 
	speciality_recordid = #{specialityRecordid}, 
	operator = #{operator}
	update_date = now(), 

where id = #{id}
