mapper.select:
		select
	        id,
	        exam_taskid,
	        exam_siteid,
	        num,
	        status,
	        operator,
	        update_date,
	        db_flag,
	        enabled_flag
		 from reg_exam_site_submit

         and enabled_flag=1
		   and id = #{id} 
		   and exam_taskid = #{examTaskid} 
		   and exam_siteid = #{examSiteid} 
		   and num = #{num} 
		   and status = #{status} 
		   and db_flag = #{dbFlag} 
		   and enabled_flag = #{enabledFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select count(*) from reg_exam_site_submit

             and enabled_flag=1
		   and id = #{id} 
		   and exam_taskid = #{examTaskid} 
		   and exam_siteid = #{examSiteid} 
		   and num = #{num} 
		   and status = #{status} 
		   and db_flag = #{dbFlag} 
		   and enabled_flag = #{enabledFlag}

mapper.update:
update reg_exam_site_submit
exam_taskid = #{examTaskid}, exam_siteid = #{examSiteid}, num = #{num}, status = #{status}, operator = #{operator}, update_date = now(), db_flag = #{dbFlag}
where id = #{id}
