mapper.select:
		select
	        id,
	        studentid,
	        speciality_recordid,
	        courseid,
	        type,
	        objid,
	        kg_grade,
	        zg_grade,
	        old_grade,
	        exam_grade,
	        school_grade,
	        grade,
	        exam_flag,
	        status,
	        use_status,
	        flag,
	        source_courseid,
	        et_dm,
	        old_studentid,
	        exam_date,
	        old_courseid,
	        school_scoreid,
	        operator,
	        update_date,
	        db_flag,
	        enabled_flag
		 from stu_score_his

         and enabled_flag=1
		   and id = #{id} 
		   and studentid = #{studentid} 
		   and speciality_recordid = #{specialityRecordid} 
		   and courseid = #{courseid} 
		   and type = #{type} 
		   and objid = #{objid} 
		   and kg_grade = #{kgGrade} 
		   and zg_grade = #{zgGrade} 
		   and old_grade = #{oldGrade} 
		   and exam_grade = #{examGrade} 
		   and school_grade = #{schoolGrade} 
		   and grade = #{grade} 
		   and exam_flag = #{examFlag} 
		   and status = #{status} 
		   and use_status = #{useStatus} 
		   and flag = #{flag} 
		   and source_courseid = #{sourceCourseid} 
		   and et_dm = #{etDm} 
		   and old_studentid = #{oldStudentid} 
		   and exam_date = #{examDate} 
		   and old_courseid = #{oldCourseid} 
		   and school_scoreid = #{schoolScoreid} 
		   and db_flag = #{dbFlag} 
		   and enabled_flag = #{enabledFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select count(*) from stu_score_his

             and enabled_flag=1
		   and id = #{id} 
		   and studentid = #{studentid} 
		   and speciality_recordid = #{specialityRecordid} 
		   and courseid = #{courseid} 
		   and type = #{type} 
		   and objid = #{objid} 
		   and kg_grade = #{kgGrade} 
		   and zg_grade = #{zgGrade} 
		   and old_grade = #{oldGrade} 
		   and exam_grade = #{examGrade} 
		   and school_grade = #{schoolGrade} 
		   and grade = #{grade} 
		   and exam_flag = #{examFlag} 
		   and status = #{status} 
		   and use_status = #{useStatus} 
		   and flag = #{flag} 
		   and source_courseid = #{sourceCourseid} 
		   and et_dm = #{etDm} 
		   and old_studentid = #{oldStudentid} 
		   and exam_date = #{examDate} 
		   and old_courseid = #{oldCourseid} 
		   and school_scoreid = #{schoolScoreid} 
		   and db_flag = #{dbFlag} 
		   and enabled_flag = #{enabledFlag}

mapper.update:
update stu_score_his
studentid = #{studentid}, speciality_recordid = #{specialityRecordid}, courseid = #{courseid}, type = #{type}, objid = #{objid}, kg_grade = #{kgGrade}, zg_grade = #{zgGrade}, old_grade = #{oldGrade}, exam_grade = #{examGrade}, school_grade = #{schoolGrade}, grade = #{grade}, exam_flag = #{examFlag}, status = #{status}, use_status = #{useStatus}, flag = #{flag}, source_courseid = #{sourceCourseid}, et_dm = #{etDm}, old_studentid = #{oldStudentid}, exam_date = #{examDate}, old_courseid = #{oldCourseid}, school_scoreid = #{schoolScoreid}, operator = #{operator}, update_date = now(), db_flag = #{dbFlag}
where id = #{id}
