mapper.select:
		select
	        id,
	        type,
	        code,
	        name,
	        certificate_no,
	        gender,
	        native_province,
	        native_county,
	        age,
	        speciality_name,
	        speciality_levels,
	        school_name,
	        graduate_date,
	        remark,
	        operator,
	        update_date,
	        enabled_flag
		 from stu_student_certificate_old

         and enabled_flag=1
		   and id = #{id} 
		   and type = #{type} 
		   and name = #{name} 

			 and code = #{code} 
			 and name = #{name} 
			 and certificate_no = #{certificateNo} 
			 and (concat(name,certificate_no,code) like concat('%',#{searchName},'%'))
		   and gender = #{gender} 
		   and native_province = #{nativeProvince} 
		   and native_county = #{nativeCounty} 
		   and age = #{age} 
		   and speciality_name = #{specialityName} 
		   and speciality_levels = #{specialityLevels} 
		   and school_name = #{schoolName} 
		   and graduate_date = #{graduateDate} 
		   and remark = #{remark} 
		   and enabled_flag = #{enabledFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select count(*) from stu_student_certificate_old

             and enabled_flag=1
		   and id = #{id} 
		   and type = #{type} 
		   and code = #{code} 
		   and name = #{name} 
		   and certificate_no = #{certificateNo} 
			 and (concat(name,certificate_no,code) like concat('%',#{searchName},'%'))
		   and gender = #{gender} 
		   and native_province = #{nativeProvince} 
		   and native_county = #{nativeCounty} 
		   and age = #{age} 
		   and speciality_name = #{specialityName} 
		   and speciality_levels = #{specialityLevels} 
		   and school_name = #{schoolName} 
		   and graduate_date = #{graduateDate} 
		   and remark = #{remark} 
		   and enabled_flag = #{enabledFlag}

mapper.update:
update stu_student_certificate_old
type = #{type}, code = #{code}, name = #{name}, certificate_no = #{certificateNo}, gender = #{gender}, native_province = #{nativeProvince}, native_county = #{nativeCounty}, age = #{age}, speciality_name = #{specialityName}, speciality_levels = #{specialityLevels}, school_name = #{schoolName}, graduate_date = #{graduateDate}, remark = #{remark}, operator = #{operator}, update_date = now()
where id = #{id}
