		   and email = #{email} 
		   and regionid = #{regionid} 
		   and child_regionid = #{childRegionid} 
		   and schoolid = #{schoolid} 
		   and groupid = #{groupid} 
		   and classify = #{classify} 
		   and status = #{status} 
		   and audit_status = #{auditStatus} 
		   and confirm_status = #{confirmStatus} 
		   and back_operator = #{backOperator} 
		   and back_date = #{backDate} 
		   and confirm_operator = #{confirmOperator} 
		   and confirm_date = #{confirmDate} 
		   and enabled_flag = #{enabledFlag} 



                order by ${sort} ${order}


                order by change_type desc



			limit #{offset}, #{limit}

		select count(*) from stu_student_log

             and enabled_flag=1
		   and change_type = #{changeType} 
