mapper.select:
		select
	        id,
	        exam_taskid,
	        size,
	        operator,
	        update_date,
	        enabled_flag
		 from reg_paper_size

         and enabled_flag=1
		   and id = #{id} 
		   and exam_taskid = #{examTaskid} 
		   and size = #{size} 
		   and enabled_flag = #{enabledFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select count(*) from reg_paper_size

             and enabled_flag=1
		   and id = #{id} 
		   and exam_taskid = #{examTaskid} 
		   and size = #{size} 
		   and enabled_flag = #{enabledFlag}

mapper.update:
update reg_paper_size
exam_taskid = #{examTaskid}, size = #{size}, operator = #{operator}, update_date = now()
where id = #{id}
