mapper.select:
		select
	        id,
	        studentid,
	        courseid,
	        grade,
	        status,
	        remark,
	        operator,
	        update_date,
	        db_flag,
	        enabled_flag
		 from stu_score_old_his

         and enabled_flag=1
		   and id = #{id} 
		   and studentid = #{studentid} 
		   and courseid = #{courseid} 
		   and grade = #{grade} 
		   and status = #{status} 
		   and remark = #{remark} 
		   and db_flag = #{dbFlag} 
		   and enabled_flag = #{enabledFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select count(*) from stu_score_old_his

             and enabled_flag=1
		   and id = #{id} 
		   and studentid = #{studentid} 
		   and courseid = #{courseid} 
		   and grade = #{grade} 
		   and status = #{status} 
		   and remark = #{remark} 
		   and db_flag = #{dbFlag} 
		   and enabled_flag = #{enabledFlag}

mapper.update:
update stu_score_old_his
studentid = #{studentid}, courseid = #{courseid}, grade = #{grade}, status = #{status}, remark = #{remark}, operator = #{operator}, update_date = now(), db_flag = #{dbFlag}
where id = #{id}
