mapper.select:
		select
			a.id,
			a.courseid,
			b.name as courseName
	    from   stu_score a left join pla_course b on a.courseid = b.id

			  and a.enabled_flag = 1
			  and a.type = 'a'
			  and a.studentid = #{studentid}

			   and a.id = #{id} 
			   and a.courseid = #{courseid} 
			   and a.name = #{courseName} 



                order by ${sort} ${order}


                order by a.id desc



			limit #{offset}, #{limit}

select count(*)
from
	stu_score a left join pla_course b on a.courseid = b.id

	and a.enabled_flag = 1 and a.type = 'a' and a.studentid = #{studentid}
	 and a.id = #{id} 
	 and a.courseid = #{courseid} 
	 and a.name = #{courseName}
