mapper.select:
		select
			a.id,
			a.studentid,
			a.speciality_recordid,
			a.courseid,
			a.grade,
			a.ratio,
			a.status,
			a.audit_status,
			a.audit_operator,
			a.audit_date,
			a.operator,
			a.update_date,
			a.enabled_flag,
			b.name as courseName
		 from stu_score_school a left join pla_course b  on a.courseid = b.id

         and a.enabled_flag=1
		   and a.id = #{id} 
		   and a.studentid = #{studentid} 
		   and a.speciality_recordid = #{specialityRecordid} 
		   and a.courseid = #{courseid} 
		   and a.grade = #{grade} 
		   and a.ratio = #{ratio} 
		   and a.status = #{status} 
		   and a.audit_status = #{auditStatus} 
		   and a.audit_operator = #{auditOperator} 
		   and a.audit_date = #{auditDate} 
		   and a.enabled_flag = #{enabledFlag} 



                order by ${sort} ${order}


                order by a.id desc



			limit #{offset}, #{limit}

		select count(*) from stu_score_school

             and enabled_flag=1
		   and id = #{id} 
		   and studentid = #{studentid} 
		   and speciality_recordid = #{specialityRecordid} 
		   and courseid = #{courseid} 
		   and grade = #{grade} 
		   and ratio = #{ratio} 
		   and status = #{status} 
		   and audit_status = #{auditStatus} 
		   and audit_operator = #{auditOperator} 
		   and audit_date = #{auditDate} 
		   and enabled_flag = #{enabledFlag}

mapper.update:
update stu_score_school
studentid = #{studentid}, speciality_recordid = #{specialityRecordid}, courseid = #{courseid}, grade = #{grade}, ratio = #{ratio}, status = #{status}, audit_status = #{auditStatus}, audit_operator = #{auditOperator}, audit_date = #{auditDate}, operator = #{operator}, update_date = now()
where id = #{id}

mapper.select:
select id
from pla_speciality_record

	and enabled_flag=1
	and specialityid = #{specialityid} 
	and schoolid = #{schoolid} 
	and collegeid = #{collegeid} 
	and direction = #{direction}
